INSERT INTO clinics (name, image_url, address, phone, email, website, description) VALUES
('Стоматология "Белый Кит"', 'https://cdn.poehali.dev/projects/2aa2b148-44c7-4f4d-95d3-92715d54ad4e/files/fedcde2a-f6c0-4a94-a74e-490854a950ec.jpg', 'ул. Баумана, 58, Казань', '+7 (843) 555-01-01', 'info@belykitdent.ru', 'https://belykitdent.ru', 'Современная стоматологическая клиника с новейшим оборудованием и командой профессионалов. Специализируемся на имплантации, протезировании и эстетической стоматологии.'),
('Дентал Клиник', 'https://cdn.poehali.dev/projects/2aa2b148-44c7-4f4d-95d3-92715d54ad4e/files/0b52ebc8-a6e9-45b8-b941-cfbd0f34dbb0.jpg', 'пр. Победы, 125, Казань', '+7 (843) 555-02-02', 'info@dentalclinic.ru', 'https://dentalclinic.ru', 'Премиальная стоматология европейского уровня. Используем только сертифицированные материалы от ведущих производителей.'),
('СмайлПлюс', 'https://cdn.poehali.dev/projects/2aa2b148-44c7-4f4d-95d3-92715d54ad4e/files/0879615b-4e06-4e60-96a3-22d4bffb27d1.jpg', 'ул. Петербургская, 45, Казань', '+7 (843) 555-03-03', 'info@smileplus.ru', 'https://smileplus.ru', 'Семейная стоматология с демократичными ценами. Работаем для всей вашей семьи - от детей до взрослых.'),
('Зубная Фея', 'https://cdn.poehali.dev/projects/2aa2b148-44c7-4f4d-95d3-92715d54ad4e/files/fedcde2a-f6c0-4a94-a74e-490854a950ec.jpg', 'ул. Пушкина, 27, Казань', '+7 (843) 555-04-04', 'info@zubnayafeya.ru', 'https://zubnayafeya.ru', 'Специализированная детская стоматология. Создаем комфортную атмосферу для маленьких пациентов.'),
('Дент Престиж', 'https://cdn.poehali.dev/projects/2aa2b148-44c7-4f4d-95d3-92715d54ad4e/files/0b52ebc8-a6e9-45b8-b941-cfbd0f34dbb0.jpg', 'ул. Чернышевского, 33, Казань', '+7 (843) 555-05-05', 'info@dentprestige.ru', 'https://dentprestige.ru', 'VIP-стоматология для требовательных клиентов. Индивидуальный подход и высочайший уровень сервиса.'),
('Стома Лайф', 'https://cdn.poehali.dev/projects/2aa2b148-44c7-4f4d-95d3-92715d54ad4e/files/fedcde2a-f6c0-4a94-a74e-490854a950ec.jpg', 'ул. Декабристов, 89, Казань', '+7 (843) 555-06-06', 'info@stomalife.ru', 'https://stomalife.ru', 'Многопрофильная стоматология с опытными врачами. Работаем на результат, который радует годами.');
