INSERT INTO reviews (clinic_id, user_id, rating, review_text, created_at) VALUES
(1, 2, 5, 'Отличная клиника! Делала имплантацию у доктора Иванова. Всё прошло безболезненно, персонал очень внимательный. Рекомендую!', '2024-11-20 10:30:00'),
(1, 3, 4, 'Хорошее оборудование, современный подход. Единственный минус - иногда приходится долго ждать приёма.', '2024-11-15 14:20:00'),
(2, 4, 5, 'Лучшая клиника в городе! Поставила виниры - результат превзошел все ожидания. Доктор Соколов - настоящий профессионал!', '2024-11-22 16:45:00'),
(3, 2, 5, 'Отличное соотношение цены и качества. Лечил зубы всей семьей. Все довольны!', '2024-11-18 11:00:00'),
(4, 3, 5, 'Прекрасная клиника для детей! Ребенок больше не боится стоматолога. Спасибо докторам!', '2024-11-10 09:15:00'),
(5, 4, 5, 'Выбираю только лучшее. Дент Престиж полностью оправдал ожидания. Профессионализм на высшем уровне!', '2024-11-23 15:30:00'),
(6, 2, 4, 'Хорошая клиника, опытные врачи. Цены средние по городу.', '2024-11-12 13:40:00');